import React from 'react';

export default function EnergyEfficiencyTracker({ project }) {
  const [consumption, setConsumption] = React.useState(3200);
  const [generation, setGeneration] = React.useState(4500);
  const [timeRange, setTimeRange] = React.useState('day');

  const efficiency = ((generation - consumption) / generation * 100).toFixed(1);
  const netEnergy = generation - consumption;
  const isPositive = netEnergy >= 0;

  const stats = [
    { label: 'Generation', value: generation + ' W', color: '#34C759' },
    { label: 'Consumption', value: consumption + ' W', color: '#FF9500' },
    { label: 'Net Energy', value: (netEnergy > 0 ? '+' : '') + netEnergy + ' W', color: isPositive ? '#34C759' : '#FF3B30' },
    { label: 'Efficiency', value: efficiency + '%', color: '#007AFF' }
  ];

  const handleConsumptionChange = (e) => setConsumption(parseInt(e.target.value));
  const handleGenerationChange = (e) => setGeneration(parseInt(e.target.value));

  return (
    <div style={styles.container}>
      <h3 style={styles.title}>Energy Efficiency Tracker</h3>

      {/* Stats Grid */}
      <div style={styles.statsGrid}>
        {stats.map(stat => (
          <div key={stat.label} style={styles.statCard}>
            <div style={styles.statLabel}>{stat.label}</div>
            <div style={{ ...styles.statValue, color: stat.color }}>{stat.value}</div>
          </div>
        ))}
      </div>

      {/* Controls */}
      <div style={styles.controls}>
        <div style={styles.controlGroup}>
          <label style={styles.label}>Generation (W)</label>
          <input
            type="range"
            min="0"
            max="10000"
            value={generation}
            onChange={handleGenerationChange}
            style={styles.slider}
          />
          <div style={styles.value}>{generation}W</div>
        </div>

        <div style={styles.controlGroup}>
          <label style={styles.label}>Consumption (W)</label>
          <input
            type="range"
            min="0"
            max="10000"
            value={consumption}
            onChange={handleConsumptionChange}
            style={styles.slider}
          />
          <div style={styles.value}>{consumption}W</div>
        </div>
      </div>

      {/* Time Range Selector */}
      <div style={styles.timeRange}>
        {['hour', 'day', 'week', 'month'].map(range => (
          <button
            key={range}
            onClick={() => setTimeRange(range)}
            style={{
              ...styles.timeButton,
              backgroundColor: timeRange === range ? '#007AFF' : '#e0e0e0',
              color: timeRange === range ? '#fff' : '#333'
            }}
          >
            {range.charAt(0).toUpperCase() + range.slice(1)}
          </button>
        ))}
      </div>

      {/* Recommendation */}
      <div style={{ ...styles.recommendation, backgroundColor: isPositive ? '#f0f9f0' : '#fff0f0' }}>
        <strong>Recommendation:</strong> {isPositive ? `You're producing ${netEnergy}W more than you consume!` : `Reduce consumption by ${Math.abs(netEnergy)}W to break even.`}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'system-ui, -apple-system, sans-serif'
  },
  title: {
    margin: '0 0 16px 0',
    fontSize: '18px',
    fontWeight: '600',
    color: '#333'
  },
  statsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
    gap: '12px',
    marginBottom: '20px'
  },
  statCard: {
    padding: '12px',
    backgroundColor: '#f5f5f5',
    borderRadius: '8px',
    textAlign: 'center'
  },
  statLabel: {
    fontSize: '12px',
    color: '#666',
    marginBottom: '6px'
  },
  statValue: {
    fontSize: '16px',
    fontWeight: '700'
  },
  controls: {
    marginBottom: '20px',
    padding: '12px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px'
  },
  controlGroup: {
    marginBottom: '16px'
  },
  label: {
    display: 'block',
    fontSize: '12px',
    fontWeight: '600',
    marginBottom: '6px',
    color: '#333'
  },
  slider: {
    width: '100%',
    height: '6px',
    borderRadius: '3px',
    background: '#ddd',
    outline: 'none',
    marginBottom: '6px'
  },
  value: {
    fontSize: '12px',
    color: '#666',
    textAlign: 'right'
  },
  timeRange: {
    display: 'grid',
    gridTemplateColumns: 'repeat(4, 1fr)',
    gap: '8px',
    marginBottom: '16px'
  },
  timeButton: {
    padding: '8px',
    border: 'none',
    borderRadius: '6px',
    fontSize: '12px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.2s'
  },
  recommendation: {
    padding: '12px',
    borderRadius: '6px',
    fontSize: '12px',
    color: '#333',
    border: '1px solid #e0e0e0'
  }
};
